module.exports = function(grunt) {
	grunt.initConfig({
		pkg: grunt.file.readJSON('package.json'),
		jshint: {
			options: {
				//curly: true,
				eqeqeq: true,
				undef: true,
				//unused: true,
				strict: true,
				latedef: 'nofunc',
				newcap: true,
				// Relaxing options
				asi: true,
			},
			tasks: {
				options: {
					node: true
				},
				files: {
					src: [ 'tasks/*.js' ]
				}
			},
		}
	});
	grunt.loadNpmTasks('grunt-contrib-jshint');
	grunt.loadTasks('tasks');

	// Lint task
	var lintTasks = ['jshint:tasks'];
	grunt.registerTask('lint', lintTasks);

	// Build a release
	grunt.registerTask('release', ['lint', 'default-release']);

	// Setup default task
	grunt.registerTask('default', ['lint']);
};